sudo apt-get install dnsmasq -y && sed -i 's/#port=5353/port=5353/g' /etc/dnsmasq.conf && sudo apt-get install git -y && git clone "https://github.com/TheWebAuthor/Dnsmasq-Adblocker.git" /$USER/adblocker && sh /$USER/adblocker/update-all.sh && echo "sh \"/"$USER"/adblocker/update-all.sh\"" > /etc/cron.daily/dnsmasq.adblocker.update
